class Calculadora {

    public static Integer suma(int i, int j) {
		// TODO Auto-generated method stub
		return i+j;
	}

    public static Integer resta(int i, int j) {
		// TODO Auto-generated method stub
		return i-j;
	}

    public static Integer multiplica(int i, int j) {
		// TODO Auto-generated method stub
		return i*j;
	}

    public static Integer divide(int i, int j) {
		// TODO Auto-generated method stub
		return i/j;
        }
    
}
